import { View, Text, StyleSheet, Image, FlatList, ScrollView } from 'react-native'
import React, {useEffect, useState} from 'react'
import img1 from '../../../assets/Imgs-profile/img1.png'
import img2 from '../../../assets/Imgs-profile/img2.png'
import img3 from '../../../assets/Imgs-profile/img3.png'
import { initializeApp } from 'firebase/app';
import { getDownloadURL, getStorage, ref } from "firebase/storage";

//FIREBASE
const firebaseConfig = {
    apiKey: "AIzaSyAh7Wr4c9nv2ON-Ysnw36E_LpHucem8Wps",
    authDomain: "enfogramtaller5.firebaseapp.com",
    projectId: "enfogramtaller5",
    storageBucket: "enfogramtaller5.appspot.com",
    messagingSenderId: "416888291101",
    appId: "1:416888291101:web:8362cbddff7e9b1de6abf4",
    measurementId: "G-6LDJPXHE6V"
  };
  
  //INICIAR FIREBASE
  const app = initializeApp(firebaseConfig);
  console.log(app)


const Picturesx3 = () => {    
    
    const [images, setImages] = useState()

    const fetchImages = async () => {
        const storage = getStorage(app);
        const imagesRef = ref(storage, 'images/1-undefined');
        await getDownloadURL(imagesRef).then((item) =>{
            setImages(item)
        })
    }

    useEffect(() => {
        fetchImages()
    }, [])
    

    return (
        <ScrollView
        showsVerticalScrollIndicator={false}
        style={{width:'100%',
                height:'auto'}}
        >

        <View style={{width:'100%',
                      height:'auto',
                      backgroundColor:'white',
                      flexWrap:'wrap',
                      flexDirection:'row',
                      justifyContent:'space-between'}}>
        <View style={{width:'32%', height:200, marginTop:0}}>
            <Image source={{uri:images}} style={{width:'100%', height:'100%', resizeMode:'stretch'}}/>
        </View>
        <View style={{width:'32%', height:200, marginTop:0}}>
            <Image source={img2} style={{width:'100%', height:'100%', resizeMode:'stretch'}}/>
        </View>
        <View style={{width:'32%', height:200, marginTop:0}}>
            <Image source={img3} style={{width:'100%', height:'100%', resizeMode:'stretch'}}/>
        </View>


        </View>

        </ScrollView>

    )
}

const styles = StyleSheet.create({})

export default Picturesx3;
