import { View, Text, StyleSheet, Image, FlatList, ScrollView } from 'react-native'
import React, { useEffect, useState } from 'react'
import { getDatabase, ref, get } from 'firebase/database'
import { initializeApp } from 'firebase/app';
import { getDownloadURL, getStorage, ref as storegeRef } from "firebase/storage";

//FIREBASE
const firebaseConfig = {
    apiKey: "AIzaSyAh7Wr4c9nv2ON-Ysnw36E_LpHucem8Wps",
    authDomain: "enfogramtaller5.firebaseapp.com",
    projectId: "enfogramtaller5",
    databaseURL: "https://enfogramtaller5-default-rtdb.firebaseio.com/",
    storageBucket: "enfogramtaller5.appspot.com",
    messagingSenderId: "416888291101",
    appId: "1:416888291101:web:8362cbddff7e9b1de6abf4",
    measurementId: "G-6LDJPXHE6V"
};

//INICIAR FIREBASE
const app = initializeApp(firebaseConfig);
console.log(app)

const Picturesx3 = () => {

    const [images, setImages] = useState([])
    const [cont, setCont] = useState([0])


    // const getCont = () => {

    //     // Get a database reference to our posts
    //     const db = getDatabase(app);
    //     const ref = db.ref('Contador');

    //     // Attach an asynchronous callback to read the data at our posts reference
    //     ref.on('value', (snapshot) => {
    //         console.log(snapshot.val());
    //     }, (errorObject) => {
    //         console.log('The read failed: ' + errorObject.name);
    //     });
    //     // const db = getDatabase(app)
    //     // const dbRef = ref(db, 'Contador')
    //     // dbRef.on("value", function (snapshot) {
    //     //     var data = snapshot.val();
    //     //     setCont(data.cont);
    //     //     console.log(cont);
    //     // })
    // }

    const fetchImages = async () => {
        const cont = 9;
        const storage = getStorage(app);
        for (let index = 0; index < cont; index++) {
            const imagesRef = storegeRef(storage, `images/${index}-undefined`);
            console.log(imagesRef);
            console.log(getDownloadURL(imagesRef));
             await getDownloadURL(imagesRef).then((item) => {
                setImages(item);
                console.log(item);
            })
        }
    }

    useEffect(() => {
        fetchImages();
        // getCont();
    }, [])


    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={{
                width: '100%',
                height: 'auto'
            }}
        >

            <View style={{
                width: '100%',
                height: 'auto',
                backgroundColor: 'white',
                flexWrap: 'wrap',
                flexDirection: 'row',
                justifyContent: 'space-between'
            }}>


                    <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                        <Image source={{ uri: images[0] }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                    </View>

                    <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                        <Image source={{ uri: images[1] }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                    </View>

                    <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                        <Image source={{ uri: images[2] }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                    </View>

                    <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                        <Image source={{ uri: images[3] }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                    </View>

                    <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                        <Image source={{ uri: images[4] }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                    </View>

                    <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                        <Image source={{ uri: images[5] }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                    </View>

                    <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                        <Image source={{ uri: images[6] }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                    </View>

            </View>

        </ScrollView>

    )
}

export default Picturesx3;
