import { View, StyleSheet, Image, FlatList, ScrollView } from 'react-native'
import React, { useEffect, useState } from 'react'
import { initializeApp } from 'firebase/app';
import { getDownloadURL, getStorage, ref } from "firebase/storage";

//FIREBASE
const firebaseConfig = {
    apiKey: "AIzaSyAh7Wr4c9nv2ON-Ysnw36E_LpHucem8Wps",
    authDomain: "enfogramtaller5.firebaseapp.com",
    projectId: "enfogramtaller5",
    databaseURL: "https://enfogramtaller5-default-rtdb.firebaseio.com/",
    storageBucket: "enfogramtaller5.appspot.com",
    messagingSenderId: "416888291101",
    appId: "1:416888291101:web:8362cbddff7e9b1de6abf4",
    measurementId: "G-6LDJPXHE6V"
};

//INICIAR FIREBASE
const app = initializeApp(firebaseConfig);
console.log(app)

var dbRef = database().ref("Contador");


const Picturesx3 = () => {

    const [images, setImages] = useState([])
    const [cont, setCont] = useState(0)

    const getCont = () => {
        dbRef.on("value", function (snapshot) {
            var data = snapshot.val();
            setCont(data.cont);
            console.log(cont);
        })
    }

    const fetchImages = async () => {
        const storage = getStorage(app);
        for (let index = 1; index < cont; index++) {
            const imagesRef = ref(storage, `images/${index}-undefined.png`);
            await getDownloadURL(imagesRef).then((item) => {
                setImages(current => [...current, item]);
            })
        }
    }



    useEffect(() => {
        fetchImages();
        getCont()
    }, [])


    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={{
                width: '100%',
                height: 'auto'
            }}
        >

            <View style={{
                width: '100%',
                height: 'auto',
                backgroundColor: 'white',
                flexWrap: 'wrap',
                flexDirection: 'row',
                justifyContent: 'space-between'
            }}>


            {images.map((image) => (
                <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                    <Image source={{ uri: image }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                </View>
                ))};

            </View>

        </ScrollView>

    )
}

const styles = StyleSheet.create({})

export default Picturesx3;
