import { View, Text, StyleSheet, Image, FlatList, ScrollView } from 'react-native'
import React, { useEffect, useState } from 'react'
import { getDatabase, ref as databaseRef} from 'firebase/database'
import { initializeApp } from 'firebase/app';
import { getDownloadURL, getStorage, ref } from "firebase/storage";

//FIREBASE
const firebaseConfig = {
    apiKey: "AIzaSyAh7Wr4c9nv2ON-Ysnw36E_LpHucem8Wps",
    authDomain: "enfogramtaller5.firebaseapp.com",
    projectId: "enfogramtaller5",
    databaseURL: "https://enfogramtaller5-default-rtdb.firebaseio.com/",
    storageBucket: "enfogramtaller5.appspot.com",
    messagingSenderId: "416888291101",
    appId: "1:416888291101:web:8362cbddff7e9b1de6abf4",
    measurementId: "G-6LDJPXHE6V"
};

//INICIAR FIREBASE
const app = initializeApp(firebaseConfig);
console.log(app)

const db = getDatabase(app)
const dbRef = databaseRef(db, 'Contador')

const Picturesx3 = () => {

    const [images, setImages] = useState([])
    const [cont, setCont] = useState([0])


    const getCont = () => {
        dbRef.on("value", function (snapshot) {
            var data = snapshot.val();
            setCont(data.cont);
            console.log(cont);
        })
    }

    const fetchImages = async () => {
        const cont = 0;
        const storage = getStorage(app);
        const imagesRef = ref(storage, `images/${cont}-undefined`);
        console.log(imagesRef);
        console.log(getDownloadURL(imagesRef));
        await getDownloadURL(imagesRef).then((item) => {
            setImages(item);
        })
    }

    useEffect(() => {
        fetchImages();
        getCont();
    }, [])


    // https://firebasestorage.googleapis.com/v0/b/enfogramtaller5.appspot.com/o/images%2F0-undefined.png
    // https://firebasestorage.googleapis.com/v0/b/enfogramtaller5.appspot.com/o/images%2F0-undefined?alt=media&token=b0945cf2-1e17-4976-8d69-6d308ca5b7b7


    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            style={{
                width: '100%',
                height: 'auto'
            }}
        >

            <View style={{
                width: '100%',
                height: 'auto',
                backgroundColor: 'white',
                flexWrap: 'wrap',
                flexDirection: 'row',
                justifyContent: 'space-between'
            }}>

                <View style={{ width: '32%', height: 200, marginTop: 0 }}>
                    <Image source={{ uri: images }} style={{ width: '100%', height: '100%', resizeMode: 'stretch' }} />
                </View>

            </View>

        </ScrollView>

    )
}

export default Picturesx3;
