import { View, Text, StyleSheet, Image, FlatList, ScrollView } from 'react-native'
import React, {useState, useEffect} from 'react'
import img1 from '../../../assets/Imgs-profile/img1.png'
import img2 from '../../../assets/Imgs-profile/img2.png'
import img3 from '../../../assets/Imgs-profile/img3.png'
import { firebaseConfig} from '../../firebase'
import { getDownloadURL } from 'firebase/storage'
import initializeApp from 'firebase/app'

initializeApp(firebaseConfig);


const Picturesx3 = () => {

    let storageRef = firebase.storage().ref()

    const fetchImages = async() => {

        let result = await storageRef.child('enfogramtaller5').listAll();
        /// map() array of the imageRef.getDownloadURL() promises 
        let urlPromises = result.items.map(imageRef => imageRef.getDownloadURL());
      
        // return all resolved promises
        return Promise.all(urlPromises);
      }


    useEffect(() => {

        fetchImages();

        const urls = fetchImages()
        setPictureURLs(urls);
        console.log("inside .then() ", urls) 

    }, [])
    


    // const [size, setSize] = useState(0);


    // const getSize = async() => {
      
