    //     const res = await fetch('http://192.168.1.2:3001');
    //     const data = await res.json();
    //     setSize(parseInt(data))
  
    //   } catch (error) {
  
    //     console.error(error);
  
    //   }
    // }
  
    // useEffect(() => {
    //   getSize()
    // }, [getSize()])
    

    return (
        <ScrollView
        showsVerticalScrollIndicator={false}
        style={{width:'100%',
                height:'auto'}}
        >

        <View style={{width:'100%',
                      height:'auto',
                      backgroundColor:'white',
                      flexWrap:'wrap',
                      flexDirection:'row',
                      justifyContent:'space-between'}}>
        <View style={{width:'32%', height:200, marginTop:0}}>
            <Image source={{uri:`https://firebasestorage.googleapis.com/v0/b/enfogramtaller5.appspot.com/o/3-undifined?alt=media&token=06998ba6-069a-4fb7-99f5-aa14d939b8e3`}} style={{width:'100%', height:'100%', resizeMode:'stretch'}}/>
        </View>
        <View style={{width:'32%', height:200, marginTop:0}}>
            <Image source={img2} style={{width:'100%', height:'100%', resizeMode:'stretch'}}/>
        </View>
        <View style={{width:'32%', height:200, marginTop:0}}>
            <Image source={img3} style={{width:'100%', height:'100%', resizeMode:'stretch'}}/>
        </View>


        </View>

        </ScrollView>

    )
}

const styles = StyleSheet.create({})

export default Picturesx3;
